$(document).ready(function () {
  AOS.init({ duration: 1500 }),
    $(window).on("load", function () {
      setTimeout(function () {
        $("#preloader").fadeOut("slow"), $("main").addClass("loaded");
      }, 1500);
    }),
    (window.sr = ScrollReveal({ reset: !1, mobile: !0, vFactor: 0.1 })),
    sr.reveal(".articleText p"),
    sr.reveal(
      ".creditItem-team",
      { duratixon: 300, delay: 200, origin: "right", distance: "5vw" },
      100
    ),
    sr.reveal(
      ".creditItem-member",
      { duration: 300, delay: 200, origin: "left", distance: "5vw" },
      100
    ),
    $(".rest_area-slider").slick({
      dots: !1,
      arrows: !0,
      autoplay: !1,
      autoplaySpeed: 1e4,
      speed: 500,
      fade: !1,
      infinite: !1,
      cssEase: "linear",
      lazyLoad: "ondemand",
      slidesPerRow: 1,
      prevArrow:
        "<button type='button' class='slick-prev slick-arrow'><img src='img/icn-arrow.svg'></button>",
      nextArrow:
        "<button type='button' class='slick-next slick-arrow'><img src='img/icn-arrow.svg'></button>",
    }),
    $(window).resize(function () {
      $(".rest_area-slider")[0].slick.refresh();
    }),
    $(".trackItem").first().addClass("active");
  var a = window.location.hash.substr(1);
  (e = $(".trackWrap").find(".active")).prop("id");
  if (((i = e.height()), $(".trackWrap").height(i), "" != a)) {
    var t = a,
      e = $(".trackWrap").find("#" + t);
    $(".trackItem").removeClass("active"),
      $(".trackItem").first().addClass("active");
    var i = e.height();
    $(".trackWrap").height(i), e.addClass("active");
  }
  $(".trackRoad").hover(
    function () {
      var a = $(this).data("target");
      $(this).addClass("hover"),
        $(".trackRoad-line[data-road=" + a + "]").addClass("hover");
    },
    function () {
      $(".trackRoad.hover").removeClass("hover"),
        $(".trackRoad-line.hover").removeClass("hover");
    }
  ),
    $("#rest_area-trigger").each(function (a, t) {
      $(this).click(function (a) {
        $("html, body").animate(
          { scrollTop: $("#rest_area-sc").offset().top - 50 },
          1e3
        );
      });
    }),
    $(".rest_areaTabs-item").on("click", function (a) {
      a.preventDefault(), $(this).toggleClass("active");
      var t = this.getAttribute("data-toggle-target");
      $(".tabContent").removeClass("active").filter(t).addClass("active");
    }),
    $(".rest_areaTabs-item:first-child").addClass("active"),
    $(".rest_areaTabs-dsc").hide(),
    $(".rest_areaTabs-dsc:first").show(),
    $(".rest_areaTabs-item").click(function () {
      $(".rest_areaTabs-item").removeClass("active"),
        $(this).addClass("active"),
        $(".rest_areaTabs-dsc").hide();
      var a = this.getAttribute("data-toggle-target");
      return $(a).fadeIn(), !1;
    }),
    $(".destinationItem").first().addClass("active");
  var s = window.location.hash.substr(1);
  (o = $(".destinationWrap").find(".active")).prop("id");
  if (((r = o.height()), $(".destinationWrap").height(r + 80), "" != s)) {
    var o = s;
    o = $(".destinationWrap").find("#" + o);
    $(".destinationItem").removeClass("active"),
      $(".destinationItem").first().addClass("active");
    var r = o.height();
    $(".destinationWrap").height(r), o.addClass("active");
  }
  $(window).scroll(function () {
    $(this).scrollTop() > 100 ? $("#scroll").fadeIn() : $("#scroll").fadeOut();
  }),
    $("#scroll").click(function () {
      return $("html, body").animate({ scrollTop: 0 }, 600), !1;
    }),
    $(window)
      .scroll(function () {
        var a = $(window).scrollTop();
        $("section").each(function (t) {
          $(this).position().top - 100 <= a &&
            ($("section.sectionActive").removeClass("sectionActive"),
            $("section").eq(t).addClass("sectionActive"),
            $("#peta_section.sectionActive").addClass("petaLine-active"),
            $("#trick_section.sectionActive").addClass("tips-active")),
            $(this).position().top <= a &&
              $("#intro_section.sectionActive .quoteInner").addClass(
                "quote-active"
              );
        });
      })
      .scroll(),
    $(document).on("click", '#menuToggle a[href^="#"]', function (a) {
      a.preventDefault(),
        $("#menuToggle").removeClass("active"),
        $("html, body").animate(
          { scrollTop: $($.attr(this, "href")).offset().top },
          500
        );
    }),
    $(document).on("click", ".menuToggle-nav", function () {
      $("#menuToggle").toggleClass("active");
    }),
    $("main").click(function () {
      $("#menuToggle.active").removeClass("active"), $("body").off("click");
    });
});
